CREATE TABLE IF NOT EXISTS economia_estados (
    nombre_entidad VARCHAR(255),
    entidad INT,
    produccion_bruta_total DECIMAL(15, 2),
    insumos_utilizados DECIMAL(15, 2),
    consumo_intermedio DECIMAL(15, 2),
    valor_agregado DECIMAL(15, 2),
    formacion_capital DECIMAL(15, 2),
    activos_fijos_adquiridos DECIMAL(15, 2)
);

